create table CatProductos
(
    Id             int not null
        constraint CatProductos_pk
            primary key,
    NombreProducto varchar(50)    not null,
    ImagenProducto image,
    PrecioUnitario decimal(18, 2) not null,
    ext            varchar(5)
)
go

create table CatTipoCliente
(
    ID          int         not null
        constraint CatTipoCliente_pk
            primary key,
    TipoCliente varchar(50) not null
)
go

create table TbIClientes
(
    id            int          not null
        constraint TbIClientes_pk
            primary key,
    RazonSocial   varchar(200) not null,
    IdTipoCliente int          not null
        constraint TbIClientes_CatTipoCliente_ID_fk
            references CatTipoCliente,
    FechaCreacion date         not null,
    RFC           varchar(50)  not null
)
go

create table TbIFacturas
(
    Id                   int            not null
        constraint TbIFacturas_pk
            primary key,
    FechaEmisionFactura  date           not null,
    IdCliente            int            not null
        constraint TbIFacturas_TbIClientes_id_fk
            references TbIClientes,
    NumeroFactura        int            not null,
    NumeroTotalArticulos int            not null,
    SubTotalFactura      decimal(18, 2) not null,
    TotalImpuesto        decimal(18, 2),
    TotalFactura         decimal(18, 2) not null
)
go

create table TbIDetallesFactura
(
    Id                     int            not null
        constraint TbIDetallesFactura_pk
            primary key,
    IdFactura              int            not null
        constraint TbIDetallesFactura_TbIFacturas_Id_fk
            references TbIFacturas,
    IdProducto             int            not null
        constraint TbIDetallesFactura_CatProductos_Id_fk
            references CatProductos,
    CantidadDeProducto     int            not null,
    PrecioUnitarioProducto decimal(18, 2) not null,
    SubtotalProducto       decimal(18, 2) not null,
    Notas                  varchar(200)
)
go

